mapper.select:
select pk_artwork_id, fk_userId, artwork_name, artwork_describe, artwork_status, logo_path, last_create_date, last_modify_date, four_letter_tips,username,fk_audit_id,logo_path_status,play_client from ecm_artwork a inner join ecm_user u on u.pk_user_id = a.fk_userId
and pk_artwork_id like CONCAT('%',#{pkArtworkId},'%')
and fk_userId like CONCAT('%',#{fkUserid},'%')
and artwork_status = #{artworkStatus}
and artwork_name like CONCAT('%',#{artworkName},'%')
and artwork_describe like CONCAT('%',#{artworkDescribe},'%')
and four_letter_tips like CONCAT('%',#{fourLetterTips},'%')
and play_client is null or play_client = #{playClient}
order by last_create_date desc limit #{start},#{limit}

select pk_artwork_id, fk_userId, artwork_name, artwork_describe, artwork_status, logo_path, logo_path_status, last_create_date, last_modify_date, four_letter_tips, fk_audit_id, play_mode, is_endings, play_client, username from ecm_artwork inner join ecm_user on fk_userId = pk_user_id
and pk_artwork_id like CONCAT('%',#{pkArtworkId},'%')
and fk_userId like CONCAT('%',#{fkUserid},'%')
and artwork_status = #{artworkStatus}
and artwork_name like CONCAT('%',#{artworkName},'%')
and artwork_describe like CONCAT('%',#{artworkDescribe},'%')
and four_letter_tips like CONCAT('%',#{fourLetterTips},'%')
and play_client is null or play_client = #{playClient}
order by last_create_date desc limit #{start},#{limit}

select count(*)
from ecm_artwork
where fk_userId =#{pkUserId}

select pk_artwork_id,fk_userId,sub_total_flow,sub_used_flow
from  ecm_artwork inner join ecm_downlink_flow  on ecm_downlink_flow.fk_user_id = ecm_artwork.fk_userId
where pk_artwork_id in #{id}

select pk_artwork_id, fk_userId, artwork_name, artwork_describe, artwork_status, logo_path, logo_path_status, last_create_date, last_modify_date, four_letter_tips, fk_audit_id, play_mode, is_endings, play_client, username , sub_total_flow ,sub_used_flow,pk_id as pkDownFowNumber from ecm_artwork inner join ecm_user on fk_userId = pk_user_id left join ecm_downlink_flow on fk_userId = fk_user_id
and pk_artwork_id like CONCAT('%',#{pkArtworkId},'%')
and fk_userId like CONCAT('%',#{fkUserid},'%')
and artwork_status = #{artworkStatus}
and artwork_name like CONCAT('%',#{artworkName},'%')
and artwork_describe like CONCAT('%',#{artworkDescribe},'%')
and four_letter_tips like CONCAT('%',#{fourLetterTips},'%')
and play_client is null or play_client = #{playClient}
order by last_create_date desc limit #{start},#{limit}

mapper.update:
update ecm_artwork
set  artwork_status = 5
where fk_userId =#{pkUserId}

update ecm_artwork
set  artwork_status = 1,fk_audit_id =null
where pk_artwork_id =#{pkArtworkId}

update ecm_artwork
set  play_client = null
where pk_artwork_id =#{pkArtworkId}
