mapper.insert:
insert into ecm_artwork (fk_userId, artwork_name, artwork_describe,
  artwork_status, logo_path, last_create_date,
  last_modify_date, four_letter_tips, fk_audit_id
  )
values (#{fkUserid,jdbcType=INTEGER}, #{artworkName,jdbcType=VARCHAR}, #{artworkDescribe,jdbcType=VARCHAR},
  #{artworkStatus,jdbcType=SMALLINT}, #{logoPath,jdbcType=VARCHAR}, #{lastCreateDate,jdbcType=TIMESTAMP},
  #{lastModifyDate,jdbcType=TIMESTAMP}, #{fourLetterTips,jdbcType=VARCHAR}, #{fkAuditId,jdbcType=INTEGER}
  )

mapper.select:
select count(*)
from ecm_artwork
where fk_userId =#{pkUserId}

mapper.update:
update ecm_artwork
set  artwork_status = 5
where fk_userId =#{pkUserId}

update ecm_artwork
