mapper.select:
select

from ecm_user
where mobile = #{phone} and `password`= #{passWord}

select  pk_user_id, `password`, email_uuid, is_valid, username, mobile, email,
`count`, last_login_time, card_code, last_check_mail, roles, user_logo_url, user_logo_status ,
sub_total_flow ,  sub_used_flow ,sub_app_id
from ecm_user left join  ecm_downlink_flow on fk_user_id = pk_user_id



        and pk_user_id like CONCAT('%',#{userId},'%')


        and username like CONCAT('%',#{userName},'%')


        and mobile like CONCAT('%',#{phone},'%')



        and is_valid = #{isValid}



        and user_logo_status = #{userLogoStatus}


        and roles like CONCAT('%',#{roles},'%')


        and sub_total_flow > #{subTotalFlow}



limit #{start},#{limit}
